const openMenuBtn=document.querySelector(".main-item"),closeMenuBtn=document.querySelector(".close-btn"),menu=document.querySelector(".sub-menu");function disableScrolling(){let n=window.scrollX,e=window.scrollY;window.onscroll=function(){window.scrollTo(n,e)}}function enableScrolling(){window.onscroll=function(){}}openMenuBtn.addEventListener("click",()=>{menu.classList.add("menu--active"),disableScrolling()}),closeMenuBtn.addEventListener("click",()=>{menu.classList.remove("menu--active"),enableScrolling()});